from collections import namedtuple

TestCase = namedtuple('TestCase', 'case expected')


def check_case(test_case, actual):
    if test_case.expected == actual:
        print("OK %s" % (test_case.case,))
    else:
        print("FAIL %s, expected %s, got %s" % (test_case.case, test_case.expected, actual))


TEST_CASES = [
    TestCase("""
     |          
     |  +--+    
     A  |  C    
 F---|----E|--+ 
     |  |  |  D 
     +B-+  +--+     
    """, 'ABCDEF'),
]


N = 0  # clockwise
E = 1
S = 2
W = 3

# sides
RIGHT = +1
LEFT = -1


def turn(direction, side):
    return (direction + side) % 4


assert turn(N, RIGHT) == E
assert turn(N, LEFT) == W
assert turn(S, RIGHT) == W
assert turn(S, LEFT) == E

Vector = namedtuple('Vector', "x y")

directions = {
    N: Vector(0, -1),  # equiv. Vector(x=0, y=-1)
    E: Vector(1, 0),
    S: Vector(0, 1),
    W: Vector(-1, 0),
}


def advance(pos, direction):
    delta = directions[direction]
    return Vector(pos.x + delta.x, pos.y + delta.y)
    # return Vector(*map(sum, zip(pos, delta)))


def get_value(maze, pos):
    try:
        current = maze[pos.y][pos.x]
        return current if current != ' ' else None
    except IndexError:
        return None


def next_pos(maze, pos, dir):
    current = get_value(maze, pos)

    if current == '+':
        next_dir = turn(dir, LEFT)
        next_pos = advance(pos, next_dir)
        if get_value(maze, next_pos):
            return next_pos, next_dir
        next_dir = turn(dir, RIGHT)
        next_pos = advance(pos, next_dir)
        if get_value(maze, next_pos):
            return next_pos, next_dir
    else:
        next_pos = advance(pos, dir)
        return next_pos if get_value(maze, next_pos) else None, dir

    return None, dir


def solve(input):
    maze = list(filter(None, input.split('\n')))
    for row in maze:
        print(row)

    pos = Vector(maze[0].index('|'), 0)
    dir = S
    visited = ''

    while pos:
        current = get_value(maze, pos)
        print(pos, dir, current)
        if current.isalpha():
            visited += current
        pos, dir = next_pos(maze, pos, dir)

    return visited


